krpano.action:
set(spots_ocultados, 0);

for(set(i,0), i LT hotspot.count, inc(i),
    copy(hotspot_name, hotspot[get(i)].name);

    if(hotspot_name != '' AND hotspot_name != 'undefined',
        set(hotspot[get(hotspot_name)].visible, false);
        inc(spots_ocultados);
    );
);

set(spots_mostrados, 0);

for(set(i,0), i LT hotspot.count, inc(i),
    copy(hotspot_name, hotspot[get(i)].name);

    if(hotspot_name != '' AND hotspot_name != 'undefined',
        set(hotspot[get(hotspot_name)].visible, true);
        inc(spots_mostrados);
    );
);

set(nombreSpot,%1);
addhotspot(%1);
set(hotspot[%1].ath,%2);
set(hotspot[%1].atv,%3);
set(hotspot[%1].keep, true); // Mantener el hotspot entre escenas
set(hotspot[%1].visible, true); // Visible por defecto
if(
    %4 == disponible, hotspot[%1].loadstyle(hs_pro_disponible),
    %4 == vendido, hotspot[%1].loadstyle(hs_pro_vendido),
    %4 == reservado, hotspot[%1].loadstyle(hs_pro_reservado),
    %4 == nodisponible, hotspot[%1].loadstyle(hs_pro_nodisponible),
);

set(hotspot[%1].html,%5);
set(hotspot[%1].onclick,mostrar_ficha_lote(%1));
